    plt.plot([13, 13], [1, 0], 'x--', lw=1.5)
    plt.text(14, 0.90, r'封城开始', fontdict={'size': '12', 'color': 'black'})

    plt.scatter(36, 1, s=50, color='cyan')
    plt.plot([36, 36], [1, 0], 'x--', lw=1.5)
    plt.text(30, 0.90, r'封城结束', fontdict={'size': '12', 'color': 'black'})
