def draw_every_indeicators(beginData,endData,
                           first_average, second_average, third_average, fourth_average,
                           first_degree,second_degree, third_degree, fourth_degree,
                           first_edge_number,second_edge_number, third_edge_number,fourth_edge_number,
                           first_nature_connecty, second_nature_connecty,third_nature_connecty, fourth_nature_connecty):
    listXData = getdaylist(beginData, endData)
    # 画图 设置X轴显示效果
    fig = plt.figure(figsize=(14, 8),dpi=450)#,dpi=450
    ax1 = fig.add_subplot(221)
    function_encapsulation(first_average, second_average, third_average, fourth_average,listXData,ax1,"(a) 咸阳") #北京

    ax2 = fig.add_subplot(222)
    function_encapsulation(first_degree,second_degree, third_degree, fourth_degree,listXData,ax2,"(b) 渭南") #天津

    ax3 = fig.add_subplot(223)
    function_encapsulation(first_edge_number,second_edge_number, third_edge_number,fourth_edge_number,listXData,ax3,"(c) 兰州") # 邢台

    ax4 = fig.add_subplot(224)
    # 为了设置x轴时间的显示
    function_encapsulation(first_nature_connecty, second_nature_connecty,third_nature_connecty, fourth_nature_connecty,listXData,ax4,"(d) 榆林") #衡水
    fig.tight_layout()
    plt.show()



