import os
import pandas as pd

def file_name_first(file_dir):
      L=[]
      for root, dirs, files in os.walk(file_dir):
         for file in files:
            if os.path.splitext(file)[1] == '.csv':
                L.append(os.path.join(root, file))
      return L

def file_name_second(file_dir):
    for root, dirs, files in os.walk(file_dir):
        print(root) #当前目录路径
        print(dirs) #当前路径下所有子目录
        print(files) #当前路径下所有非目录子文件
    return files

def get_dir_dateName(fileDirctPath):
    filelistCsv = file_name_second(fileDirctPath)
    lastfinaldate = []
    for i in filelistCsv:
        lastfinaldate.append(i[0:8])
    return lastfinaldate
