def tocsvProblenDate(X,Y):

    dict ={}
    for date ,number in zip(X,Y):
        dict[date] = number

    df = pd.DataFrame.from_dict(dict, orient='index', columns=['数值'])
    df = df.reset_index().rename(columns={'index': '日期'})
    df.to_csv("D:\\04python project\\01-爬虫-爬取百度迁徙数据\\physicalconnecitvity\\findproblemdata\\problemdata_largeest_component.csv",index=False,encoding="utf-8-sig")


